import React, { useState } from 'react';

const questions = [
  {
    question: 'What is the capital of France?',
    options: ['Rome', 'Madrid', 'Paris', 'Berlin'],
    answer: 'Paris',
  },
  {
    question: 'Which planet is known as the Red Planet?',
    options: ['Venus', 'Mars', 'Jupiter', 'Saturn'],
    answer: 'Mars',
  },
  {
    question: 'Who wrote "Romeo and Juliet"?',
    options: ['Mark Twain', 'William Shakespeare', 'Charles Dickens', 'Leo Tolstoy'],
    answer: 'William Shakespeare',
  },
  {
    question: 'What is the smallest prime number?',
    options: ['0', '1', '2', '3'],
    answer: '2',
  },
];

function QuizGame() {
  const [step, setStep] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleAnswerOptionClick = (option) => {
    setSelectedOption(option);
    if (option === questions[step].answer) {
      setScore(score + 1);
    }
  };

  const handleNext = () => {
    setSelectedOption(null);
    if (step < questions.length - 1) setStep(step + 1);
    else setShowScore(true);
  };

  const handleRestart = () => {
    setStep(0);
    setScore(0);
    setShowScore(false);
    setSelectedOption(null);
  };

  return (
    <div className="flex flex-col items-center justify-center p-6 bg-gradient-to-br from-pink-100 to-red-100 max-w-md mx-auto rounded-lg shadow-lg min-h-screen">
      <h2 className="text-4xl font-extrabold mb-6 text-red-700">Quiz Game</h2>
      {showScore ? (
        <div className="text-center">
          <p className="text-2xl font-bold mb-4">You scored {score} out of {questions.length}</p>
          <button
            onClick={handleRestart}
            className="px-6 py-3 bg-red-600 text-white rounded-lg font-bold shadow hover:bg-red-700 transition"
          >
            Restart Quiz
          </button>
        </div>
      ) : (
        <>
          <div className="mb-6 text-lg font-semibold">{questions[step].question}</div>
          <div className="flex flex-col gap-3 w-full max-w-sm">
            {questions[step].options.map(option => (
              <button
                key={option}
                onClick={() => handleAnswerOptionClick(option)}
                className={`py-3 rounded-lg font-semibold transition 
                  ${selectedOption === option 
                    ? option === questions[step].answer ? 'bg-green-400 text-white' : 'bg-red-400 text-white'
                    : 'bg-white hover:bg-pink-200'}
                `}
                disabled={!!selectedOption}
              >
                {option}
              </button>
            ))}
          </div>
          <button
            onClick={handleNext}
            disabled={!selectedOption}
            className={`mt-6 px-6 py-3 font-bold rounded-lg shadow text-white transition 
              ${selectedOption ? 'bg-red-600 hover:bg-red-700' : 'bg-red-300 cursor-not-allowed'}
            `}
          >
            {step === questions.length -1 ? 'Finish' : 'Next'}
          </button>
        </>
      )}
      <button
        onClick={() => { setSelectedGame(null); setGameMode(null); }}
        className="mt-4 px-8 py-3 bg-gray-600 text-white font-bold text-lg rounded-full shadow-lg hover:bg-gray-700 transition-all duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-gray-300"
      >
        Back to Mode Selection
      </button>
    </div>
  );
}

export default QuizGame;
