import React, { useState } from 'react';

const Games = () => {
  const [selectedGame, setSelectedGame] = useState(null);

  const renderGame = () => {
    if (selectedGame === 'tic-tac-toe') {
      return (
        <iframe
          title="Tic Tac Toe"
          src="https://playtictactoe.org/"
          width="100%"
          height="500px"
          frameBorder="0"
        ></iframe>
      );
    }
    if (selectedGame === 'memory') {
      return (
        <iframe
          title="Memory Game"
          src="https://www.memozor.com/memory-games/for-kids/animals-no-time"
          width="100%"
          height="500px"
          frameBorder="0"
        ></iframe>
      );
    }
    return null;
  };

  return (
    <section id="games">
      <h2>Games</h2>
      <div style={{ display: 'flex', gap: '20px' }}>
        <button onClick={() => setSelectedGame('tic-tac-toe')}>Play Tic Tac Toe</button>
        <button onClick={() => setSelectedGame('memory')}>Play Memory Game</button>
      </div>
      <div style={{ marginTop: '20px' }}>
        {renderGame()}
      </div>
    </section>
  );
};

export default Games;
